package day4;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		int b=89;
		int c=0;
		System.out.println(a);
		System.out.println(b);
		c=a;
		a=b;
		b=c;
		System.out.println(a);
		System.out.println(b);
		int i=87;
		int j=54;
		System.out.println(i);
		System.out.println(j);
		i=i+j;
		j=i-j;
		i=i-j;
		System.out.println(i);
		System.out.println(j);
	}

}
