package day6;

public class swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println(a);
		System.out.println(b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println(a);
		System.out.println(b);
		int i=54;
		int j=86;
		int k=0;
		System.out.println(i);
		System.out.println(j);
		k=i;
		i=j;
		j=k;
		System.out.println(i);
		System.out.println(j);
		
		
		
	}

}
